import React, { Component } from 'react';
import ImageUpload from './components/ImageUpload'
import './App.css';
const ColorThief = require('colorthief');



class App extends Component {
  constructor() {
    super()
    this.state = {
      allImages: null,
      file: []
    }
  }

  DomContentLoaded = () => {
    fetch("http://localhost:3001/images").then(r => r.json()).then(allImages => this.setState({ allImages }));
  }

  handleFile = (file) => {
    const formData = new FormData();
    formData.append("file", file[0], file[0].name);
    this.setState({ file: formData })
    const args = { html: file[0].name, pinned: false }
    const image = { html: file[0].name, pinned: false, img: formData  }

    console.log(image)
    fetch(`http://localhost:3001/images`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "Accept": "application/json"
      },
      body: JSON.stringify(image)
    }).then(r => r.json())
      .then(lol => {
        console.log(lol)
      })
  }

  render() {
    return (
      <div className="App" >
        < ImageUpload onFilesAdded={this.handleFile} />
      </div>
    );
  }

};


export default App;
