          </span>
          <span className="Splotch" >
            <DisplayColors />
          </span>
          <span>
            <DisplayFeatureImage featureImage={this.state.featuredImage} featuredClick={this.featureClick}/>
          </span>
        </div>
        <MatchedImages allImages={this.state.allImages} chooseFeatured={this.chooseFeaturedImage}/>
      </div>
    )
    }

};


export default App;
