import React, { Component } from 'react';
import DisplayColors from './DisplayColors'
import DisplayFeatureImage from './DisplayFeatureImage'
import MatchedImages from './MatchedImages'



class ImageUpload extends Component {
    constructor(props){
        super(props)
    // Ref is assigned to instance property when component is 
    // constructed so it can be referenced throughout the component.
            this.state = { highlight: false };
            this.fileInputRef = React.createRef();

    // using .bind instead of arrow functions in order to have access to this.
            this.openFileDialog = this.openFileDialog.bind(this);
            this.onFilesAdded = this.onFilesAdded.bind(this);
            this.onDragOver = this.onDragOver.bind(this);
            this.onDragLeave = this.onDragLeave.bind(this);
            this.onDrop = this.onDrop.bind(this);
    }

    openFileDialog = () => {
        // The click() method simulates a mouse - click on an element.
        if (this.props.disabled) return;
        this.fileInputRef.current.click();
    }

    onFilesAdded = (e) => {
        if (this.props.disabled) return;
        const files = e.target.files;
        if (this.props.onFilesAdded) {
            const array = this.fileListToArray(files);
            // console.log(array);
            this.props.onFilesAdded(array)
        }
     }

     fileListToArray = (list) => {
         const array = [];
         for (var i=0; i < list.length; i++) {
             array.push(list.item(i));
         }
        //  console.log(array);
         return array;
     }

     onDragOver = (e) => {
        e.preventDefault();
        if (this.props.disabled) return;
        this.setState({highlight: true})
     }

    onDragLeave = () => {
        this.setState({ highlight: false })
    }

    onDrop = (e) => {
        e.preventDefault();
        if (this.props.disabled) return;
console.log(e, "in onDrop")
        const files = e.dataTransfer.files;
        if (this.props.onFilesAdded) {
            const array = this.fileListToArray(files);
            this.props.onFilesAdded(array);
        }
        this.setState({ highlight: false })
    }


    render() {
        return (
            <div 
                className={`Dropzone ${this.state.highlight ? "Highlight" : ""}`}
                onDragOver={this.onDragOver}
                onDragLeave={this.onDragLeave}
                onDrop={this.onDrop}
                onClick={this.openFileDialog} 
                style={{cursor: this.props.disabled ? "default" : "pointer"}}
            >

                <input 
                ref={this.fileInputRef}
                className={"FileInput"}
                type={"file"}
                multiple onChange={this.onFilesAdded}
                />

                <h1>image upload</h1>
                    <div className={"Card"} >
                        <span className="Splotch" >
                            < DisplayColors />
                        </span>
                        <span className="Splotch" >
                            < DisplayColors />
                        </span>
                        <span className="Splotch" >
                            < DisplayColors />
                        </span>
                        <span>
                            < DisplayFeatureImage />
                        </span>
                    </div>
                    < MatchedImages />
            </div>
        );
    }
}

export default ImageUpload;
